// Input: binary HTML at any key (e.g., data_html, data, file) or json.html/text
// Output later: json.visibleText

const item = $input.first() || { json:{}, binary:{} };

// ---- Load HTML (robust) ----
let html = '';

// 1) Prefer any binary property that has .data
if (item.binary && typeof item.binary === 'object') {
  for (const [k, v] of Object.entries(item.binary)) {
    if (v && typeof v.data === 'string' && v.data.length) {
      html = Buffer.from(v.data, 'base64').toString('utf8');
      break;
    }
  }
}

// 2) Fallbacks: json.html, json.text, json.visibleText
if (!html && typeof item.json?.html === 'string') html = item.json.html;
if (!html && typeof item.json?.text === 'string') html = item.json.text;
if (!html && typeof item.json?.visibleText === 'string') html = item.json.visibleText;

// 3) If still nothing, throw with helpful debug
if (!html) {
  const jKeys = Object.keys(item.json || {});
  const bKeys = Object.keys(item.binary || {});
  throw new Error(
    'No HTML/text found on input. Expect binary (e.g., data_html/data) or json.html/text. ' +
    `json keys: [${jKeys.join(', ')}], binary keys: [${bKeys.join(', ')}]`
  );
}

// ---- Helpers ----
function decodeEntities(str) {
  return str.replace(/&(#(x)?([\da-fA-F]+)|[a-zA-Z]+);/g, (m, inner, isHex, num) => {
    if (inner[0] === '#') {
      const code = isHex ? parseInt(num,16) : parseInt(num,10);
      return Number.isFinite(code) ? String.fromCodePoint(code) : m;
    }
    const map = {nbsp:' ',amp:'&',lt:'<',gt:'>',quot:'"',apos:"'",ensp:' ',emsp:' '};
    return map[inner] ?? m;
  });
}

// ---- Strip "invisible" stuff, keep what users see ----
let s = html.replace(/<!--[\s\S]*?-->/g, '');
s = s.replace(/<(script|style|template|noscript|head)\b[\s\S]*?<\/\1\s*>/gi, '');
s = s.replace(/<(pre|code)\b[\s\S]*?<\/\1\s*>/gi, '');
s = s.replace(/<img[^>]*alt=['"]([^'"]+)['"][^>]*>/gi, ' $1 ');
s = s.replace(/<br\s*\/?>/gi, '\n');
s = s.replace(/<\/(p|div|section|article|header|footer|address|blockquote|h[1-6])\s*>/gi, '\n');
s = s.replace(/<li\b[^>]*>/gi, '\n- ');
s = s.replace(/<\/(ul|ol)\s*>/gi, '\n');
s = s.replace(/<t[hd]\b[^>]*>/gi, '\t');
s = s.replace(/<\/tr\s*>/gi, '\n');
s = s.replace(/<\/table\s*>/gi, '\n');
s = s.replace(/<[^>]+>/g, ' ');

s = decodeEntities(s)
  .replace(/\r/g, '')
  .replace(/[ \t]+/g, ' ')
  .replace(/[ \t]+\n/g, '\n')
  .replace(/\n{3,}/g, '\n\n')
  .trim();

return [{ json: { visibleText: s, length: s.length } }];

