parameters.jsCode:
// Input: binary HTML at $binary.data (or data_html). Output:
//  - json.visibleText: user-visible text with [x]/[ ] markers
//  - json.controls: structured list of inputs (type/name/value/checked/label)

const item = $input.first() || { json:{}, binary:{} };

let html = '';
if (item.binary?.data?.data) {
  html = Buffer.from(item.binary.data.data, 'base64').toString('utf8');
} else if (item.binary?.data_html?.data) {
  html = Buffer.from(item.binary.data_html.data, 'base64').toString('utf8');
} else if (typeof item.json?.html === 'string') {
  html = item.json.html;
} else {
  throw new Error('Feed this node the ORIGINAL HTML as binary (e.g., Read Files â†’ data).');
}

// --- utils ---
const decodeEntities = (str) =>
  str.replace(/&(#(x)?([\da-fA-F]+)|[a-zA-Z]+);/g, (m, inner, isHex, num) => {
    if (inner[0] === '#') {
      const code = isHex ? parseInt(num,16) : parseInt(num,10);
      return Number.isFinite(code) ? String.fromCodePoint(code) : m;
    }
    const map = {nbsp:' ',amp:'&',lt:'<',gt:'>',quot:'"',apos:"'",ensp:' ',emsp:' '};
    return map[inner] ?? m;
  });

const stripTags = (s) => decodeEntities(
  s.replace(/<[^>]+>/g, ' ')
    .replace(/\r/g,'')
    .replace(/[ \t]+/g,' ')
    .replace(/[ \t]+\n/g,'\n')
    .replace(/\n{3,}/g,'\n\n')
).trim();

const getInnerText = (s) => stripTags(s);

// --- 1) sanitize (but keep labels/inputs) ---
let h = html
  .replace(/<!--[\s\S]*?-->/g, '')
  .replace(/<(script|style|template|noscript|head)\b[\s\S]*?<\/\1\s*>/gi, '');

// --- 2) map <label for="id"> text ---
const labelFor = {};
h.replace(/<label\b[^>]*for=["']([^"']+)["'][^>]*>([\s\S]*?)<\/label>/gi, (m, id, inner) => {
  labelFor[id] = getInnerText(inner);
  return m;
});

// --- 3) annotate inputs with [x]/[ ] + label ---
const controls = [];
h = h.replace(/<input\b[^>]*type\s*=\s*(?:"(radio|checkbox)"|'(radio|checkbox)'|(radio|checkbox))[^>]*>/gi,
  (tag, t1, t2, t3, offset) => {
    const type = (t1 || t2 || t3 || '').toLowerCase();
    const attrs = {};
    tag.replace(/\s([a-zA-Z0-9:-]+)(?:\s*=\s*(".*?"|'.*?'|[^\s>]+))?/g, (m, k, v) => {
      k = k.toLowerCase();
      if (v == null) { attrs[k] = true; }
      else {
        if ((v.startsWith('"') && v.endsWith('"')) || (v.startsWith("'") && v.endsWith("'"))) v = v.slice(1,-1);
        attrs[k] = v;
      }
      return m;
    });

    const id = attrs.id || '';
    const name = attrs.name || '';
    const value = attrs.value || '';
    const checked = ('checked' in attrs) || String(attrs['aria-checked']).toLowerCase() === 'true';

    // label: prefer <label for=...>, else nearby text after the input (span/text)
    let label = id && labelFor[id] ? labelFor[id] : '';
    if (!label) {
      const after = h.slice(offset + tag.length, offset + tag.length + 300);
      const m1 = after.match(/^\s*(?:<\/?\w+[^>]*>)*\s*(?:<span[^>]*>)?([^<]{1,160})/i);
      if (m1) label = m1[1].trim();
      // or if input is inside a label: grab preceding text within ~200 chars
      if (!label) {
        const before = h.slice(Math.max(0, offset - 200), offset);
        const m2 = before.match(/<label[^>]*>(?:[\s\S]*?)$/i);
        if (m2) {
          const m3 = after.match(/^[\s\S]*?<\/label>/i);
          const inline = (m2 ? m2[0] : '') + (m3 ? m3[0] : '');
          const cleaned = getInnerText(inline.replace(tag, ''));
          if (cleaned) label = cleaned;
        }
      }
    }

    const marker = checked ? '[x]' : '[ ]';
    controls.push({ type, name, value, checked, label });
    const fallback = name ? (value ? `${name}=${value}` : name) : '';
    return `${marker} ${label || fallback}`.trim();
  }
);

// --- 4) render to visible text ---
let s = h;
// line breaks for block elements
s = s.replace(/<br\s*\/?>/gi, '\n')
     .replace(/<\/(p|div|section|article|header|footer|address|blockquote|h[1-6])\s*>/gi, '\n')
     .replace(/<li\b[^>]*>/gi, '\n- ')
     .replace(/<\/(ul|ol)\s*>/gi, '\n')
     .replace(/<t[hd]\b[^>]*>/gi, '\t')
     .replace(/<\/tr\s*>/gi, '\n')
     .replace(/<\/table\s*>/gi, '\n');
// drop remaining tags and tidy
s = stripTags(s);

return [{
  json: {
    visibleText: s,
    controls, // structured list (you can group by name below if you want)
    selectedByName: controls.reduce((acc, c) => {
      if (!acc[c.name || '']) acc[c.name || ''] = [];
      if (c.checked) acc[c.name || ''].push(c.label || c.value || '');
      return acc;
    }, {})
  }
}];

